    $direction_data = getPathInfoBetweenLocations($requestDataArr);
    $distance_google_directions = $direction_data['distance'] / 1000;
    if ($isFareEstimate == "0") {
        $comparedDist = ($distance_google_directions * 85) / 100;
        if ($isReturnArr == true) {
