function converToTz($time, $toTz, $fromTz, $dateFormat = "Y-m-d H:i:s")
{
    $date = new DateTime($time, new DateTimeZone($fromTz));
    $date->setTimezone(new DateTimeZone($toTz));
    $time = $date->format($dateFormat);
    return $time;
}

