    global $PUBNUB_PUBLISH_KEY, $PUBNUB_SUBSCRIBE_KEY, $uuid, $obj, $iServiceId, $intervalmins, $PUBNUB_DISABLED, $EVENT_MSG_OBJ, $MODULES_OBJ, $LANG_OBJ, $RIDER_REQUEST_ACCEPT_TIME;
