        $whereCabType .= " AND tr.dBooking_date BETWEEN (CONVERT_TZ('".$dDate_Orig ." 00:00:00', 'SYSTEM', '".$TimeZoneOffset."')) AND (CONVERT_TZ('".$dDate_Orig ." 23:59:59', 'SYSTEM', '".$TimeZoneOffset."'))";*/
