    $fieldName = "";
    foreach ($orgtimingArray as $orgvalue) {
        $fieldName .= $orgvalue . ",";
    }
    $fieldName = rtrim($fieldName, ",");
    //Added By HJ On 15-07-2020 For Optimization company Table Query Start
