            $EarningAmount = $returnArr['fTotalGenerateFare'] - ($returnArr['fDeliveryCharge'] + $returnArr['fCommision'] + $returnArr['fOutStandingAmount'] + $returnArr['fOffersDiscount']) - $returnArr['fTipAmount'];
