                $Displaytext = $languageLabelsArr["LBL_ORDER_REFUND_TEXT"] . "\n" . $fDriverPaidAmountTxt;
            }
        }
    }
    // $returnArr['Displaytext'] = $Displaytext;
    return $Displaytext;
}

