function FetchAvailableDrivers($sourceLat, $sourceLon, $address_data = array(), $DropOff = "No", $From_Autoassign = "No", $Check_Driver_UFX = "No", $Check_Date_Time = "", $destLat = "", $destLon = "", $param1 = "", $param2 = '', $param3 = "")
