function set_hour_min($times)
{
    $hour = 0;
    $second = 0;
    $minute = floor($times / 60);
    if ($times < 60) {
        $minute = 0;
    }
    if ($minute > 60) {
        $hour = floor($minute / 60);
        $minute = floor($minute % 60);
    } else {
        $second = floor($times % 60);
    }
    $ansdata = array("hour" => $hour, "minute" => $minute, "second" => $second);
    return $ansdata;
}

