function DateTimeAdmin($text, $time = 'yes')
{
    if ($text == "" || $text == "0000-00-00 00:00:00" || $text == "0000-00-00") return "---";
    $date = @date('jS F, Y', @strtotime($text));
    if ($time == 'yes') {
        $date .= " " . @date('h:i a', @strtotime($text));;
    }
    return $date;
}

